INSERT INTO entity.user_metrics (user_id, metric_type, value)
SELECT u.id, 'PRODUCT_ADD_COUNT', 0 FROM entity.users u WHERE u.user_name = 'admin'
    ON CONFLICT (user_id, metric_type) DO NOTHING;

